mapper.update:
UPDATE BOARD
SET BOARDRECOMMENDCOUNT = NVL(BOARDRECOMMENDCOUNT, 0) + 1
WHERE BOARDID = #{boardId}

UPDATE BOARD
SET
    BOARDTITLE = #{boardTitle},
    BOARDCONTENT = #{boardContent}
WHERE BOARDID = #{boardId}

mapper.select:
SELECT * FROM BOARD
WHERE BOARDTYPE = #{boardType}
ORDER BY boardId DESC

SELECT * FROM (
SELECT ROWNUM rnum, b.*
FROM (SELECT * FROM BOARD ORDER BY boardId DESC) b
WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}

SELECT * FROM (
SELECT ROWNUM rnum, b.*
FROM (SELECT * FROM BOARD WHERE BOARDTYPE = #{boardType} ORDER BY boardId DESC) b
WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}
