mapper.update:
UPDATE BOARD
SET BOARDVIEWCOUNT = NVL(boardViewCount, 0) + 1
WHERE BOARDID = #{boardId}

UPDATE BOARD
SET BOARDTITLE   = #{boardTitle},
    BOARDCONTENT = #{boardContent}
WHERE BOARDID = #{boardId}

mapper.select:
SELECT COUNT(*)
FROM BOARD
WHERE BOARDTYPE = #{boardType}

SELECT COUNT(*)
FROM BOARD_LIKE
WHERE BOARDID = #{boardId}
  AND BOARDTYPE = #{boardType}
  AND MEMNO = #{memNo}

mapper.delete:
DELETE
FROM BOARD_LIKE
WHERE BOARDID = #{boardId}
  AND BOARDTYPE = #{boardType}
  AND MEMNO = #{memNo}

mapper.select:
SELECT COUNT(*)
FROM BOARD_LIKE
WHERE BOARDID = #{boardId}
  AND BOARDTYPE = #{boardType}
