mapper.insert:
INSERT INTO COMMENTS (
    COMMENTSID, BOARDID, BOARDTYPE, COMMENTSCONTENT,
    COMMENTSNAME, COMMENTSDATE, MEMNO, PARENTBOARDID
) VALUES (
             SEQ_COMMENTS.NEXTVAL, #{boardId}, #{boardType}, #{commentsContent},
             #{commentsName}, SYSDATE, #{memNo}, #{parentBoardId}
         )

mapper.select:
SELECT * FROM COMMENTS
WHERE BOARDID = #{boardId}
ORDER BY COMMENTSID ASC
