mapper.insert:
INSERT INTO MOVIE_CAST (
	person_id, movie_id, character_name, cast_order
) VALUES (
	#{person_id}, #{movie_id}, #{character_name}, #{cast_order}
)

mapper.select:
SELECT COUNT(*)
FROM MOVIE_CAST
WHERE person_id = #{person_id} AND movie_id = #{movie_id}

SELECT person_id
FROM MOVIE_CAST
WHERE movie_id = #{movie_id}
